function eliminarSeccion(u){
    $('.eliminarS').css('display', 'block');
    $('.contentEliminarS').show(300);

    if($('.selS').children('option').length==0){
    for(var i = 0; i<u.length; ++i){
        $('.selS').append('<option value="'+ u[i].id +'">'+ u[i].nombre +'</option>');
    }
    }

    $('.contentCambiarC').hide();
    $('.contentAgregarS').hide();
    $('.contentOrdenarS').hide();
    $('.contentEliminarP').hide();
    $('.contentAgregarP').hide();

}

