function llenarDatos(datos){

  if($('.selCampo').find(":selected").text()=="Cargo"){
    $('#nombreValor').val(datos.cargo);
  }
  if($('.selCampo').find(":selected").text()=="Departamento"){
    $('#nombreValor').val(datos.departamento);
  }
  if($('.selCampo').find(":selected").text()=="Facultad"){
    $('#nombreValor').val(datos.facultad);
  }
  if($('.selCampo').find(":selected").text()=="Teléfono"){
    $('#nombreValor').val(datos.telefono);
  }
  if($('.selCampo').find(":selected").text()=="Fax"){
    $('#nombreValor').val(datos.fax);
  }
  if($('.selCampo').find(":selected").text()=="Compañía"){
    $('#nombreValor').val(datos.compania);
  }
  if($('.selCampo').find(":selected").text()=="Correo"){
    $('#nombreValor').val(datos.correo);
  }
}

