function cambiarClave(){
    $('.cambiarC').css('display', 'block');
    $('.contentCambiarC').show();
    $('.contentEliminarS').hide();
    $('.contentAgregarS').hide();
    $('.contentEliminarP').hide();
    $('.contentAgregarP').hide();
}

function cancelarC(){
    $('.contentCambiarC').hide();
}

function agregarSeccion() {
    $('.agregarS').css('display', 'block');
    $('.contentAgregarS').css('display', 'block');

    $('.contentCambiarC').hide();
    $('.contentEliminarS').hide();
    $('.contentEliminarP').hide();
    $('.contentAgregarP').hide();

}

function eliminarSeccion(u){
    $('.eliminarS').css('display', 'block');
    $('.contentEliminarS').css('display', 'block');

    if($('.selS').children('option').length==0){
    for(var i = 0; i<u.length; ++i){
        $('.selS').append('<option value="'+ u[i].id +'">'+ u[i].nombre +'</option>');
    }
    }

    $('.contentCambiarC').hide();
    $('.contentAgregarS').hide();
    $('.contentEliminarP').hide();
    $('.contentAgregarP').hide();

}

function cancelarS(){
  if($('.contentAgregarS').css('display') != 'none'){
    $('.contentAgregarS').hide();
  }
  if($('.contentEliminarS').css('display') != 'none'){
    $('.contentEliminarS').hide();
  }
}

function agregarPublicacion() {
    $('.agregarP').css('display', 'block');
    $('.contentAgregarP').css('display', 'block');

    $('.contentCambiarC').hide();
    $('.contentEliminarS').hide();
    $('.contentAgregarS').hide();
    $('.contentEliminarP').hide();

}

function eliminarPublicacion(u, p){
    $('.eliminarP').css('display', 'block');
    $('.contentEliminarP').css('display', 'block');

    if($('.selP').children('option').length==0){
    for(var i = 0; i<u.length; ++i){
        $('.selP').append('<option value="'+ u[i].id +'">'+ u[i].nombre +'</option>');
    }
    //console.log(p);
    for(var i = 0; i<p[$('.selP option:first').text()].length; ++i){
        $('.selPS').append('<option value="'+ p[$('.selP option:first').text()][i].id +'">'+ p[$('.selP option:first').text()][i].nombre +'</option>');
    }
    }

    $('.contentCambiarC').hide();
    $('.contentEliminarS').hide();
    $('.contentAgregarS').hide();
    $('.contentAgregarP').hide();

}

function cancelarP(){
  if($('.addP').css('display') != 'none'){
    $('.contentAgregarP').hide();
  }

  if($('.delP').css('display') != 'none'){
    $('.contentEliminarP').hide();
  }
}

function llenarP(u){
    $('.selPS').empty();

      for(var i = 0; i<u[$('.selP').find(":selected").text()].length; ++i){
          $('.selPS').append('<option value="'+ u[$('.selP').find(":selected").text()][i].id +'">'+ u[$('.selP').find(":selected").text()][i].nombre +'</option>');
      }

}
